Get-ChildItem -Path "$env:userprofile\scoop\buckets\scoop-apps\bucket" -File -Recurse -Filter "*-UserInstall.json" -Exclude "*Nightly-*","*NightlyCLI-*" | Select-Object BaseName | ForEach-Object { If(!(test-path -PathType container ("$env:userprofile\scoop\apps\" + $_.Basename))) {scoop install $_.BaseName}}
Get-ChildItem -Path "$env:userprofile\scoop\buckets\scoop-apps\bucket" -File -Recurse -Filter "*-UserManualInstall.json" -Exclude "*Nightly-*","*NightlyCLI-*" | Select-Object BaseName | ForEach-Object { If(!(test-path -PathType container ("$env:userprofile\scoop\apps\" + $_.Basename))) {scoop install $_.BaseName}}
Get-ChildItem -Path "$env:userprofile\scoop\buckets\scoop-apps\bucket" -File -Recurse -Filter "*-Install.json" -Exclude "*Nightly-*","*NightlyCLI-*" | Select-Object BaseName | ForEach-Object { If(!(test-path -PathType container ("C:\ProgramData\scoop\apps\" + $_.Basename))) {scoop install $_.BaseName -g}}
Get-ChildItem -Path "$env:userprofile\scoop\buckets\scoop-apps\bucket" -File -Recurse -Filter "*-ManualInstall.json" -Exclude "*Nightly-*","*NightlyCLI-*" | Select-Object BaseName | ForEach-Object { If(!(test-path -PathType container ("C:\ProgramData\scoop\apps\" + $_.Basename))) {scoop install $_.BaseName -g}}